mapper.update:
    		UPDATE BoardImg
			SET imgname=#{imgname}
			WHERE communitynumber =#{communitynumber}

mapper.select:
select * FROM FLEAMARKETMEMBER m,friend f,profile pro WHERE f.following=m.email AND f.following=pro.email and 1=1 AND (m.email LIKE '%'||#{keyword}||'%' OR m.nickname LIKE '%'||#{keyword}||'%')
AND myemail=#{myemail}
AND following=#{myemail}

mapper.delete:
DELETE FROM friend
WHERE myemail=#{myemail}
AND FOLLOWING=#{following}

mapper.select:
SELECT * FROM capplicaion
WHERE email=#{email}

	AND category=#{category}

SELECT * 
FROM capplicaion c, reply r
WHERE c.COMMUNITYNUMBER = r.COMMUNITYNUMBER

	AND c.email=#{email}


	AND r.email=#{email}
