mapper.sql:
where (name || email || nickname) like '%'||#{title}||'%'


		and authority !='관리자'


		and authority = #{search}

mapper.select:
SELECT count(*) FROM FLEAMARKETMEMBER f where (name || email || nickname) like '%'||#{title}||'%'
and authority !='관리자'
and authority = #{search}

SELECT a.* FROM (select rownum cnt,f.*
from FLEAMARKETMEMBER f
where (name || email || nickname) like '%'||#{title}||'%'


		and authority !='관리자'


		and authority = #{search}


) a
WHERE cnt BETWEEN #{start} AND #{end}

mapper.update:
	UPDATE FLEAMARKETMEMBER
	SET NICKNAME = #{nickname},
	PHONENUMBER = #{phonenumber},
	ADDRESS = #{address},
	category = #{category}

    ,authority=#{authority},businessnumber=#{businessnumber}

	where email = #{email}

UPDATE PROFILE
SET PROFILEIMG = #{profileimg}
WHERE EMAIL = #{email}

UPDATE FLEAMARKETMEMBER
SET PASSWORD = #{password}
WHERE EMAIL=#{email}

mapper.select:
    		SELECT f.*,p.PROFILEIMG as profileimgname FROM FLEAMARKETMEMBER f,PROFILE p
			WHERE f.EMAIL(+) = p.EMAIL
			AND f.email = #{email}
